declare @currentDatabase nvarchar(128) = DB_Name()
if @currentDatabase != 'GSS_live' and @currentDatabase != 'GSS_stage' and @currentDatabase != 'GSS_test' and @currentDatabase != 'GSS' and DB_ID('GSS') is null
	create database GSS
else 
	print 'Database already exists.'

go
use -- the proper database below according to the current environment
	GSS
 -- GSS_test
 -- GSS_stage
 -- GSS_live

IF EXISTS (SELECT * FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = 'CompanyProfiles')
BEGIN
	print 'GSS*.dbo.CompanyProfiles table already exists; execution halted.'
	SET NOEXEC ON
END

CREATE TABLE [dbo].[ChangeRecords] (
    [Id] [uniqueidentifier] NOT NULL,
    [CompanyId] [int] NOT NULL,
    [CompanyVersion] [int] NOT NULL,
    [PrincipleTemplateId] [int],
    [IssueIndicatorTemplateId] [int],
    [Type] [int] NOT NULL,
    [Timestamp] [datetime] NOT NULL,
    [PreviousValue] [nvarchar](max),
    [Author] [nvarchar](100) NOT NULL,
    CONSTRAINT [PK_dbo.ChangeRecords] PRIMARY KEY ([Id])
)
CREATE TABLE [dbo].[Companies] (
    [Id] [int] NOT NULL,
    [InternalId] [uniqueidentifier] NOT NULL,
    [ResearchEntityId] [int],
    [Name] [nvarchar](100) NOT NULL,
    [Isin] [nvarchar](50) NOT NULL,
    [Description] [nvarchar](max) NOT NULL,
    [Country] [nvarchar](50) NOT NULL,
    [SubPeerGroup] [nvarchar](100) NOT NULL,
    [Website] [nvarchar](255) NOT NULL,
    [IsParkedForResearchSince] [datetime],
    [UnGlobalCompactSignatorySince] [datetime],
    [IsResearchEntityCandidate] [bit] NOT NULL,
    [Analyst] [nvarchar](100) NOT NULL,
    [Reviewer] [nvarchar](100) NOT NULL,
    CONSTRAINT [PK_dbo.Companies] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_ResearchEntityId] ON [dbo].[Companies]([ResearchEntityId])
CREATE TABLE [dbo].[CompanyProfiles] (
    [Id] [uniqueidentifier] NOT NULL,
    [CompanyId] [int] NOT NULL,
    [Version] [int] NOT NULL,
    [Status] [int] NOT NULL,
    [LastUpdated] [datetime] NOT NULL,
    [LastUpdatedBy] [nvarchar](100) NOT NULL,
    [Assessment] [int] NOT NULL,
    [HasSignificantAssessmentChange] [bit] NOT NULL,
    [AssessmentEffectiveSinceQuarterNumerator] [int] NOT NULL,
    [AssessmentEffectiveSinceQuarterYear] [int] NOT NULL,
    [PreviouslyNonCompliant] [nvarchar](300) NOT NULL,
    [OverallConclusion] [nvarchar](500) NOT NULL,
    [OutlookAssessment] [int],
    [OutlookAssessmentEffectiveSinceQuarterNumerator] [int],
    [OutlookAssessmentEffectiveSinceQuarterYear] [int],
    [OutlookAssessmentComments] [nvarchar](300) NOT NULL,
    [Engagement] [int],
    [EngagementAsOf] [datetime],
    CONSTRAINT [PK_dbo.CompanyProfiles] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_CompanyId] ON [dbo].[CompanyProfiles]([CompanyId])
CREATE TABLE [dbo].[Principles] (
    [Id] [uniqueidentifier] NOT NULL,
    [TemplateId] [int] NOT NULL,
    [CompanyProfileId] [uniqueidentifier] NOT NULL,
    [Assessment] [int] NOT NULL,
    [AssessmentEffectiveSinceQuarterNumerator] [int] NOT NULL,
    [AssessmentEffectiveSinceQuarterYear] [int] NOT NULL,
    [LastUpdated] [datetime] NOT NULL,
    [LastUpdatedBy] [nvarchar](100) NOT NULL,
    CONSTRAINT [PK_dbo.Principles] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_TemplateId] ON [dbo].[Principles]([TemplateId])
CREATE INDEX [IX_CompanyProfileId] ON [dbo].[Principles]([CompanyProfileId])
CREATE TABLE [dbo].[PrincipleTemplates] (
    [Id] [int] NOT NULL,
    [Code] [nvarchar](3) NOT NULL,
    [Name] [nvarchar](50) NOT NULL,
    [Description] [nvarchar](300) NOT NULL,
    [NormCode] [int] NOT NULL,
    CONSTRAINT [PK_dbo.PrincipleTemplates] PRIMARY KEY ([Id])
)
CREATE TABLE [dbo].[Sources] (
    [Id] [uniqueidentifier] NOT NULL,
    [Headline] [nvarchar](max) NOT NULL,
    [Publisher] [nvarchar](450) NOT NULL,
    [PublicationDate] [datetime] NOT NULL,
    [CompanyProfileId] [uniqueidentifier] NOT NULL,
    CONSTRAINT [PK_dbo.Sources] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_CompanyProfileId] ON [dbo].[Sources]([CompanyProfileId])
CREATE TABLE [dbo].[IssueIndicators] (
    [Id] [uniqueidentifier] NOT NULL,
    [TemplateId] [int] NOT NULL,
    [PrincipleId] [uniqueidentifier] NOT NULL,
    [Assessment] [int] NOT NULL,
    [AssessmentEffectiveSinceQuarterNumerator] [int] NOT NULL,
