)
CREATE INDEX [IX_TemplateId] ON [dbo].[IssueIndicators]([TemplateId])
CREATE INDEX [IX_PrincipleId] ON [dbo].[IssueIndicators]([PrincipleId])
CREATE TABLE [dbo].[ConclusionComments] (
    [Id] [uniqueidentifier] NOT NULL,
    [Text] [nvarchar](1200) NOT NULL,
    [SortKey] [int] NOT NULL,
    [IssueIndicatorId] [uniqueidentifier] NOT NULL,
    CONSTRAINT [PK_dbo.ConclusionComments] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_IssueIndicatorId] ON [dbo].[ConclusionComments]([IssueIndicatorId])
CREATE TABLE [dbo].[ImpactComments] (
    [Id] [uniqueidentifier] NOT NULL,
    [Text] [nvarchar](1200) NOT NULL,
    [SortKey] [int] NOT NULL,
    [IssueIndicatorId] [uniqueidentifier] NOT NULL,
    CONSTRAINT [PK_dbo.ImpactComments] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_IssueIndicatorId] ON [dbo].[ImpactComments]([IssueIndicatorId])
CREATE TABLE [dbo].[ManagementComments] (
    [Id] [uniqueidentifier] NOT NULL,
    [Text] [nvarchar](1200) NOT NULL,
    [SortKey] [int] NOT NULL,
    [IssueIndicatorId] [uniqueidentifier] NOT NULL,
    CONSTRAINT [PK_dbo.ManagementComments] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_IssueIndicatorId] ON [dbo].[ManagementComments]([IssueIndicatorId])
CREATE TABLE [dbo].[IssueIndicatorTemplates] (
    [Id] [int] NOT NULL,
    [Name] [nvarchar](250) NOT NULL,
    [PrincipleId] [int] NOT NULL,
