)
CREATE INDEX [IX_PrincipleId] ON [dbo].[IssueIndicatorTemplates]([PrincipleId])
CREATE TABLE [dbo].[OecdGuidelines] (
    [Id] [int] NOT NULL IDENTITY(0,1),
