ALTER TABLE [dbo].[ManagementComments] ADD CONSTRAINT [FK_dbo.ManagementComments_dbo.IssueIndicators_IssueIndicatorId] FOREIGN KEY ([IssueIndicatorId]) REFERENCES [dbo].[IssueIndicators] ([Id]) ON DELETE CASCADE
